import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { add, remove } from '../redux/Slices/cartSlices';
import toast from 'react-hot-toast';

const Card = (props) => {
    const product = props.product;
    // let title = product.title.slice(0,17);
    const cart=useSelector((state)=>state.cart);
    const dispatch=useDispatch();
  return (
    <div className='mb-40 border w-[18rem] h-[25rem] p-3 flex flex-col items-center justify-center '>
        <div>
            <p>{product.title}</p>
        </div>
        <div>
            <p>{product.description}</p>
        </div>
        <div >
            <img className='h-36 w-auto' src={product.image} alt='error'/>
        </div>
        <div>
            <p>{product.price}</p>
        </div>
        <div>            {
                cart.some((p)=>p.id===product.id)?
                (<button onClick={()=>{
                    dispatch(remove(product.id))
                    toast.error("Remove Successful")
                }}>Remove Item</button>):
                (<button  onClick={()=>dispatch(add(product))}  >Add Item</button>)
            }
        </div>

        
    
    </div>

  )
}

export default Card
