import React, { useEffect, useState } from 'react'
import { FaCartShopping } from "react-icons/fa6";
import { useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';

const Navbar = () => {
  const cart = useSelector((state) => state.cart || []); 

  
  return (
    <div className=' text-xl flex flex-row  max-w-10xl w-screen h-auto p-2 justify-evenly items-center text-white'>
      <div>
        <NavLink to='/'>
        <img src='/logo.png' alt='error' className='h-12 w-auto'></img>
        </NavLink>
      </div>
        <div className='flex flex-row justify-between items-center gap-x-5 '>
          <NavLink to='/'><p>Home</p></NavLink>
          <div className='relative flex justify-center items-center' >
            <NavLink className='relative' to='/cart'><FaCartShopping />
              <span className=' text-xs bg-green-400 rounded-full px-1.5 py-0.5'>{cart.length}</span>
            </NavLink>
           
          </div>
            
        </div>
    </div>
  )
}

export default Navbar
