import { createSlice } from "@reduxjs/toolkit";
const initialState = {
    value : 8,
}
const cartSlice = createSlice({
    name:cart,
    initialState,
    reducers:{
        increment:(state)=>{
            state.value+=9;
        }
    }
})
export default cartSlice.reducer
export const {increment}=cartSlice.actions
