import React, { useEffect, useState } from 'react'
import { FaCartShopping } from "react-icons/fa6";
import { useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';

const Navbar = () => {
  const{cart}=useSelector((state)=>state.ca);
  const[l,setL]=useState(0);
  useEffect(()=>{
    setL(cart.length);
  },[cart])
  console.log(cart.lenght)
  return (
    <div className='font-bold text-2xl flex flex-row border-2 w-[100vw] h-auto p-2 justify-evenly items-center'>
      <div>
        <NavLink to='/'>
        <img src='/logo.png' alt='error' className='h-16 w-auto'></img>
        </NavLink>
      </div>
        <div className='flex flex-row justify-between items-center gap-x-5 '>
          <NavLink to='/'><p>Home</p></NavLink>
            <NavLink to='/cart'><FaCartShopping /></NavLink>
            
        </div>
    </div>
  )
}

export default Navbar
