import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { decrement, increment } from '../redux/CounterSlices';

const Counter = () => {
    const count = useSelector((state)=>state.counter.value)
    const dispatch = useDispatch();

  return (
    <div className='flex flex-row gap-x-2 border p-5 rounded-xl bg-cyan-100'>
        <button className='border rounded-lg py-2 px-3 bg-cyan-200 shadow-lg' onClick={()=>dispatch(increment())}>
            Increment
        </button>
        <p className='border text-xl font-semibold w-12 flex justify-center items-center rounded-lg py-2 px-3 bg-cyan-00 shadow-lg'>{count}</p>
        <button className='border rounded-lg py-2 px-3 bg-cyan-200 shadow-lg' onClick={()=>dispatch(decrement())}>
            Decrement
        </button>
    </div>
  )
}

export default Counter
