import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Link} from 'react-router-dom';
import CartItem from '../components/CartItem';

const Cart = () => {
  const[totalAmount,setTotalAmount]=useState(0);
  const cart=useSelector((state)=>state.cart); 
  useEffect(()=>{
    setTotalAmount(cart.reduce((acc,curr)=>acc+curr.price,0));
  },[cart])
  return (
    <div className='flex flex-row gap-x-10 my-8 '>
      <div>
      {
        cart.length>0?(<div>
          {
            cart.map((item,index)=>(
              <CartItem key={cart.id} index={index} item={item} />
            ))
          }
        </div>):(<div><p>Cart Empty</p>
          <br/>
          <Link to='/'><button className='bg-green-700 text-white py-3 px-24 mt-6 rounded-xl '>Shop Now</button></Link>
          </div>
        )
      }
      </div>
      { cart.length>0&&
      <div className='flex mt-16 flex-col '>
         <div className=' mb-10'>
          <p className='text-green-700 font-semibold text-xl uppercase'>Your Cart</p>
         
         <p className='text-5xl text-green-700 font-bold  mb-6'>SUMMARY</p>
         <p className='font-semibold text-xl'>Total Items : <span>{
            cart.length>0?cart.length:0
          }</span></p>
         </div>
         <div className='font-semibold text-xl'>
          <p>Total Amount : <span className='font-bold'>${totalAmount}</span></p>
          <button className='bg-green-700 text-white py-3 px-24 mt-6 rounded-xl '>Checkout now</button>
         </div>
      </div>
}
      
    </div>
  )
}

export default Cart
