import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { add, remove } from '../redux/Slices/cartSlices';

const Card = (props) => {
    const product = props.product;
    // let title = product.title.slice(0,17);
    // const{cart}=useSelector((state)=>state);
    const dispatch=useDispatch();
  return (
    <div className='mb-10 border w-[18rem] h-[25rem] p-3 flex flex-col items-center justify-center'>
        <div>
            <p>{product.title}</p>
        </div>
        <div>
            <p>{product.description}</p>
        </div>
        <div >
            <img className='h-36 w-auto' src={product.image} alt='error'/>
        </div>
        <div>
            <p>{product.price}</p>
        </div>
        <button>
            {/* {
                cart.some((p)=>p.id===product.id)?(
                <button onClick={()=>dispatch(add(product))} >Remove Item</button>):
                (<button onClick={()=>dispatch(remove(product.id))} >Add Item</button>)
            } */}
        </button>
    
    </div>

  )
}

export default Card
