import React from 'react';
import { FaCartShopping } from "react-icons/fa6";
import { useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';

const Navbar = () => {
  const cart = useSelector((state) => state.cart || []); 

  return (
    <div className='text-xl flex flex-row max-w-screen-xl w-screen h-auto p-2 justify-evenly items-center text-white'>
      <div>
        <NavLink to='/'>
          <img src='/logo.png' alt='logo' className='h-12 w-auto' />
        </NavLink>
      </div>
      <div className='flex flex-row justify-between items-center gap-x-5'>
        <NavLink to='/'>
          <p>Home</p>
        </NavLink>
        <div className='relative'>
          <NavLink to='/cart' className='relative'>
            <FaCartShopping className='text-xl' />
            {/* Badge for cart items */}
            {cart.length > 0 && (
              <span className='absolute top-0 right-0 bg-green-400 text-xs rounded-full px-2 py-0.5 text-white'>
                {cart.length}
              </span>
            )}
          </NavLink>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
