import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { add, remove } from '../redux/Slices/cartSlices';
import toast from 'react-hot-toast';

const Card = (props) => {
    const product = props.product;
    let title = `${product.title.slice(0,17)}...`;
    let desc = `${product.description.slice(0,50)}...`;

    const cart=useSelector((state)=>state.cart);
    const dispatch=useDispatch();
  return (
    <div className=' border-2 py-3  h-[25rem] w-[17rem] flex flex-col items-center px-6 rounded-lg shadow-lg '>
        <div>
            <p  className='font-semibold text-xl'>{title}</p>
      
            <p className='text-xs text-black opacity-80 my-4'>{desc}</p>
        <div >
        </div>
            <img className='h-44 w-auto' src={product.image} alt='error'/>
        </div>
        <div className='flex items-center justify-between w-full border'>
            <p>{product.price}</p>
            <div>            {
                cart.some((p)=>p.id===product.id)?
                (<button onClick={()=>{
                    dispatch(remove(product.id))
                    toast.success("Remove Successful")
                }}>Remove Item</button>):
                (<button  onClick={()=>{
                    dispatch(add(product))
                    toast.success("Add Successful")
                }}  >Add Item</button>)
            }
        </div>
        </div>
       

        
    
    </div>

  )
}

export default Card
