import React, { useState, useEffect } from 'react';
import { MdDelete } from "react-icons/md";
import { useDispatch } from 'react-redux';
import { remove } from '../redux/Slices/cartSlices';

const CartItem = ({ item }) => {
  const dispatch = useDispatch();
  const [desc, setDesc] = useState("");

  const adjustDescription = () => {
    const width = window.innerWidth;
    if (width < 768) {
      setDesc(`${item.description.slice(0, 20)}...`);
    } else if (width < 1024) {
      setDesc(`${item.description.slice(0, 100)}...`);
    } else {
      setDesc(`${item.description.slice(0, 126)}...`);
    }
  };

  useEffect(() => {
    // पहली बार डिस्क्रिप्शन एडजस्ट करें
    adjustDescription();

    // रिसाइज़ इवेंट लिसनर जोड़ें
    window.addEventListener("resize", adjustDescription);

    // Cleanup listener
    return () => {
      window.removeEventListener("resize", adjustDescription);
    };
  }, [item.description]);

  return (
    <div className='mb-10 border-b-8 rounded-2xl w-[40vw] h-[16rem] p-3 flex flex-row items-center justify-center gap-x-4'>
      <div>
        <img className='h-52 w-44' alt='error' src={item.image} />
      </div>
      <div className='w-[30vw] px-4'>
        <p className='text-xl font-semibold'>{item.title}</p>
        <p className='text-sm my-6 px-2 text-black opacity-80'>{desc}</p>
        <div className='w-full flex justify-between text-2xl'>
          <p className='text-green-500 text-lg font-bold'>${item.price}</p>
          <button
            onClick={() => dispatch(remove(item.id))}
            className='text-red-900 rounded-full p-2 bg-pink-300'
          >
            <MdDelete />
          </button>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
